import React from 'react';
import './SignUp.scss';
import { connect } from 'react-redux';
import { signUpStart } from '../../redux/user/user.saga';
import FormInput from '../FormInput/FormInput';
import CustomButton from '../CustomButton/CustomButton';

class SignUp extends React.Component {
  constructor() {
    super();

    this.state = {
      displayName: '',
      email: '',
      password: '',
      confirmPassword: ''
    }
  }

  handleSubmit = async event => {
    event.preventDefault();
    const { signUpStart } = this.props;
    const { displayName, email, password, confirmPassword } = this.state;

    if (password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    signUpStart(displayName, email, password, confirmPassword);
  };

  handleChange = event => {
    const { name, value } = event.target;

    this.setState({ [name]: value });
  }

  render() {
    const { displayName, email, password, confirmPassword } = this.state;
    return (
      <section className="sign-up">
        <h2 className='title'>I do not have an account</h2>
        <p>Sign up with your email and password</p>
        <form className='sign-up-form' onSubmit={this.handleSubmit}>
          <fieldset>
            <FormInput
              type='text'
              name='displayName'
              value={displayName}
              onChange={this.handleChange}
              label='Display Name'
              required
            />
            <FormInput
              type='email'
              name='email'
              value={email}
              onChange={this.handleChange}
              label='Emails'
              required
            />
            <FormInput
              type='password'
              name='password'
              value={password}
              onChange={this.handleChange}
              label='Password'
              required
            />
            <FormInput
              type='password'
              name='confirmPassword'
              value={confirmPassword}
              onChange={this.handleChange}
              label='Confirm Password'
              required
            />
            <CustomButton type='submit'>Sign up</CustomButton>
          </fieldset>
        </form>
      </section>
    )
  }
};

const mapDispatchToProps = dispatch => ({
  googleSignUpStart: () => dispatch(googleSignUpStart()),
  emailSignUpStart: (displayName, email, password, confirmPassword) =>
    dispatch(emailSignUpStart({ displayName, email, password, confirmPassword }))
});

export default SignUp;
