import { takeEvery, call, put } from 'redux-saga/effects';

import {
  firestore,
  convertCollectionsSnapshotToMap
} from '../../firebase/firebase.utils';

import {
  fetchCollectionsSuccess,
  fetchCollectionsFailure
} from './shop.actions';

import ShopActionTypes from './shop.types';

// "All generator functions must have yields in them." 
// – video 182, Redux Saga, 5:30 mark
export function* fetchCollectionsAsync() {
  yield console.log('I am firing!');

  try {
    const collectionRef = firestore.collection('collections');
    const snapshot = yield collectionRef.get();
    const collectionsMap =
      yield call(
        convertCollectionsSnapshotToMap,
        snapshot
      );

  } catch (error) {

  }


  // 'fetchCollectionsStart' then switches our reducer state 'isFetching' on 'shop.reducer.js' to 'true'
  // 'fetchCollectionsStart' then begins the async request below
  // collectionRef
  //   .get()
  //   .then(snapshot => {
  //     const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
  //     dispatch(fetchCollectionsSuccess(collectionsMap));
  //   }).catch(error => dispatch(fetchCollectionsFailure(error.message)));
}

export function* fetchCollectionsStart() {
  yield takeEvery(
    ShopActionTypes.FETCH_COLLECTIONS_START,
    fetchCollectionsAsync
  );
}
